import React, { useEffect, useState } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import axios from "axios";
import "./App.css";

const RestaurantDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [restaurant, setRestaurant] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:5400/restaurants/${id}`)
      .then((response) => {
        setRestaurant(response.data.restaurant);
        setLoading(false);
      })
      .catch((error) => {
        setError("Failed to fetch restaurant details.");
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <button onClick={() => navigate(-1)} >⬅ Go Back</button>

      <h1>{restaurant.restaurant}</h1>
      <img src={restaurant.image || "/default-restaurant.jpg"} alt={restaurant.restaurant} className="restaurant-detail-image" />
      <p >📍 Location: {restaurant.city}</p>

      {/* See More Restaurants in this City */}
      <p>
        <Link to={`/getRestaurantsByCity/${restaurant.city}`}>
          🔍 See more restaurants in {restaurant.city}
        </Link>
      </p>
    </div>
  );
};

export default RestaurantDetails;
