import React, { useEffect, useState } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import axios from "axios";
import "./App.css";

const RestaurantDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [restaurant, setRestaurant] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:5400/restaurants/${id}`)
      .then((response) => {
        setRestaurant(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError("Failed to fetch restaurant details.");
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p className="loading">Loading...</p>;
  if (error) return <p className="error">{error}</p>;

  return (
    <div className="restaurant-detail-container">
      <button onClick={() => navigate(-1)} className="back-button">‚¨Ö Go Back</button>

      <h1 className="restaurant-title">{restaurant.restaurant}</h1>
      <img src={restaurant.image || "/default-restaurant.jpg"} alt={restaurant.restaurant} className="restaurant-detail-image" />
      <p className="restaurant-city">üìç Location: {restaurant.city}</p>

      {/* See More Restaurants in this City */}
      <p className="see-more">
        <Link to={`/getRestaurantsByCity/${restaurant.city}`}>
          üîç See more restaurants in {restaurant.city}
        </Link>
      </p>

      {/* Reviews Section (Already Implemented) */}
    </div>
  );
};

export default RestaurantDetails;
