import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import RestaurantsByCity from "./RestaurantsByCity"; // Your existing component for cities
import axios from "axios"; // Assuming you are using axios for API calls

function RestaurantSearch() {
  const [searchCity, setSearchCity] = useState("");
  const [selectedCity, setSelectedCity] = useState("");
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!searchCity && !selectedCity) {
      setError("Please select or enter a city");
      return;
    }

    setLoading(true);
    setError(null);

    const city = searchCity || selectedCity; // Use either the entered city or selected city

    try {
      const response = await axios.get(`/api/restaurants?city=${city}`);
      setRestaurants(response.data); // Assuming the response data contains the list of restaurants
    } catch (err) {
      setError("Failed to fetch restaurants.");
    } finally {
      setLoading(false);
    }
  };

  const handleCityChange = (e) => {
    setSelectedCity(e.target.value);
  };

  return (
    <div className="hero-section">
      <h1>Find the best restaurants, caf√©s, and bars</h1>
      <form onSubmit={handleSearch} className="search-form">
        <RestaurantsByCity /> {/* Existing city selection component */}
        <input
          type="text"
          placeholder="Enter city name..."
          value={searchCity}
          onChange={(e) => setSearchCity(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>

      {/* City Selection Dropdown */}
      <div>
        <label>Select a City: </label>
        <select onChange={handleCityChange} value={selectedCity}>
          <option value="">-- Select --</option>
          {["New York", "Los Angeles", "Chicago"].map((city, index) => (
            <option key={index} value={city}>
              {city}
            </option>
          ))}
        </select>
      </div>

      {/* Loading & Error Messages */}
      {loading && <p className="loading">Loading restaurants...</p>}
      {error && <p className="error">{error}</p>}

      {/* Restaurant List */}
      <div className="restaurant-list">
        {restaurants.length > 0 ? (
          restaurants.map((restaurant) => (
            <div key={restaurant.id} className="restaurant-card">
              {/* Link to restaurant details page */}
              <Link to={`/restaurant/${restaurant.id}`} className="restaurant-link">
                <img
                  src={restaurant.image || "/default-restaurant.jpg"} // Default image if not provided
                  alt={restaurant.restaurant}
                  className="restaurant-image"
                />
                <div className="restaurant-details">
                  <h2>{restaurant.restaurant}</h2>
                  <p>üìç {restaurant.city}</p>
                </div>
              </Link>
            </div>
          ))
        ) : (
          <p>No restaurants found.</p>
        )}
      </div>
    </div>
  );
}

export default RestaurantSearch;
