import { useLocation, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function Results() {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const city = queryParams.get("city");

  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRestaurants = async () => {
      try {
        const response = await axios.get(`http://localhost:5400/getRestaurantsByCity/${city}`);
        console.log("response-->>>",response);
        setRestaurants(response.data.restaurantList);
      } catch (err) {
        setError("Failed to fetch restaurants.");
      } finally {
        setLoading(false);
      }
    };

    if (city) {
      fetchRestaurants();
    }
  }, [city]);

  return (
    <div>
      <h1>Restaurants in {city}</h1>

      {loading && <p className="loading">Loading restaurants...</p>}
      {error && <p className="error">{error}</p>}

      <div className="restaurant-list">
        {restaurants.length > 0 ? (
          restaurants.map((restaurant) => (
            <div key={restaurant.id} className="restaurant-card">
              {/* Link to restaurant details page */}
              <Link to={`/restaurant/${restaurant.id}`} className="restaurant-link">
                <img
                  src={restaurant.image || "/default-restaurant.jpg"} // Default image if not provided
                  alt={restaurant.restaurant}
                  className="restaurant-image"
                />
                <div className="restaurant-details">
                  <h2>{restaurant.restaurant}</h2>
                  <p>üìç {restaurant.city}</p>
                </div>
              </Link>
            </div>
          ))
        ) : null}
      </div>
    </div>
  );
}

export default Results;
